body.script:
//

// strict mode
"use strict";

// --------------------------------------------------------------------
// PreLoader
// --------------------------------------------------------------------
$("#preloader").delay(200).fadeOut("slow");


// --------------------------------------------------------------------
// TextRotator
// --------------------------------------------------------------------
$(".rotate").textrotator({
animation: "dissolve", // You can pick the way it animates when rotating through words. Options are dissolve (default), fade, flip, flipUp, flipCube, flipCubeUp and spin.
separator: "||", 
speed: 2000 // How many milliseconds until the next word show.
});

// --------------------------------------------------------------------
// Loadmore
// --------------------------------------------------------------------
$('#linkloadmore').on('click',function(event){

event.preventDefault();

const elload = $("#Blog1_pagination-old");
const link = elload.attr('href');
const alreadyClicked = elload.data('clicked');

elload.data('clicked', true);

if (alreadyClicked) {
return false;
}

$('#linkloadmore a span').html('Memuat <i class="fa fa-spinner fa-spin fa-fw"/>');

$.get(link,function(data) {

const source = $(data).find('.blog-posts').length ? $(data) : $('<div></div>');
const clone = source.find('#Blog1_pagination-old').clone();

$(".blog-posts").append(source.find('.blog-posts').html());

if (clone.html() === undefined) {
$(".linkloadmore").html('');	
}else {		
$("#linkloadmore").html(clone);
}

elload.data('clicked', false);
}, "html");

});

